import React, { useState } from "react";
import { Link } from "react-router-dom";
import { getImageUrl } from "../../utils/imageHelper";
import { useCart } from "../../context/CartContext";
import { ShoppingCart, Heart } from "lucide-react";

export default function ProductCard({ product }) {
  const [showOverlay, setShowOverlay] = useState(false);
  const [isFavorite, setIsFavorite] = useState(false);
  const { addToCart } = useCart();

  // Handle image path - support both uploaded images (/uploads/...) and static paths (assets/img/...)
  const imageUrl = getImageUrl(product.image);

  return (
    <div className="group bg-white border border-gray-200 rounded-lg overflow-hidden hover:shadow-lg transition-shadow duration-300">
      {/* Image Container */}
      <Link
        to={`/product/${product.id}`}
        className="block relative h-64  overflow-hidden cursor-pointer"
        onMouseEnter={() => setShowOverlay(true)}
        onMouseLeave={() => setShowOverlay(false)}
      >
        <img
          src={imageUrl}
          alt={product.name}
          className="w-full h-full object-cover group-hover:scale-110 transition-transform duration-300"
          onError={(e) => {
            e.target.src = "assets/img/placeholder.png";
          }}
        />

        {/* Favorite Button */}
        <button
          onClick={(e) => {
            e.preventDefault();
            setIsFavorite(!isFavorite);
          }}
          className="absolute top-3 right-3 p-2 bg-white rounded-full shadow-md hover:shadow-lg transition-all"
        >
          <Heart
            className={`h-5 w-5 ${
              isFavorite ? "fill-red-500 text-red-500" : "text-gray-400 hover:text-red-500"
            }`}
          />
        </button>

        {/* Overlay - Add to Cart */}
        {showOverlay && (
          <div className="absolute inset-0 bg-black bg-opacity-40 flex items-end justify-center p-4 transition-opacity duration-200">
            <button
              onClick={(e) => {
                e.preventDefault();
                addToCart(product, 1);
              }}
              className="w-full px-4 py-3 bg-blue-600 text-white rounded-lg font-semibold hover:bg-blue-700 transition-colors flex items-center justify-center gap-2"
            >
              <ShoppingCart className="h-5 w-5" />
              Add to Cart
            </button>
          </div>
        )}
      </Link>

      {/* Product Info */}
      <div className="p-4">
        <Link
          to={`/product/${product.id}`}
          className="block text-sm font-medium text-gray-500 mb-1 hover:text-gray-700"
        >
          Product
        </Link>
        <Link
          to={`/product/${product.id}`}
          className="block text-lg font-semibold text-gray-900 truncate hover:text-blue-600 transition-colors mb-2"
        >
          {product.name}
        </Link>
        <div className="flex items-center justify-between">
          <p className="text-2xl font-bold text-gray-900">
            â‚¹{product.price}
          </p>
        </div>
        <Link
          to={`/product/${product.id}`}
          className="block w-full mt-4 py-2 bg-gray-100 text-gray-900 rounded-lg font-semibold hover:bg-gray-200 transition-colors text-center text-sm"
        >
          View Details
        </Link>
      </div>
    </div>
  );
}
