import React, { useState, useEffect } from "react";
import { productService } from "../../services/api";
import { Loader, X, Plus, Edit2, Save, Trash2, Upload, AlertCircle } from "lucide-react";
import { getImageUrl } from "../../utils/imageHelper";

export default function ProductImageManager({ productId, onImageAdded }) {
  const [images, setImages] = useState([]);
  const [loading, setLoading] = useState(false);
  const [uploading, setUploading] = useState(false);
  const [message, setMessage] = useState(null);
  const [newImageFile, setNewImageFile] = useState(null);
  const [newImageDescription, setNewImageDescription] = useState("");
  const [editingId, setEditingId] = useState(null);
  const [editingDescription, setEditingDescription] = useState("");
  const fileInputRef = React.useRef(null);

  // Fetch images on component mount
  useEffect(() => {
    if (productId) {
      fetchImages();
    }
  }, [productId]);

  const fetchImages = async () => {
    try {
      setLoading(true);
      const res = await productService.images.getAll(productId);
      setImages(res.data.images || []);
      setMessage(null);
    } catch (err) {
      console.error("Error fetching images:", err);
      setMessage({ type: "error", text: "Failed to load images" });
    } finally {
      setLoading(false);
    }
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      if (!file.type.startsWith("image/")) {
        setMessage({ type: "error", text: "Please select a valid image file." });
        return;
      }
      if (file.size > 5 * 1024 * 1024) {
        setMessage({ type: "error", text: "Image size must be less than 5MB." });
        return;
      }
      setNewImageFile(file);
      setMessage(null);
    }
  };

  const handleAddImage = async (e) => {
    e.preventDefault();
    e.stopPropagation();
    
    if (!newImageFile) {
      setMessage({ type: "error", text: "Please select an image file." });
      return;
    }

    try {
      setUploading(true);
      const formData = new FormData();
      formData.append("image", newImageFile);
      formData.append("angle_description", newImageDescription || "Product Image");

      const response = await productService.images.add(productId, formData);
      
      setMessage({ type: "success", text: "Image added successfully!" });
      setNewImageFile(null);
      setNewImageDescription("");
      
      // Clear file input
      if (fileInputRef.current) {
        fileInputRef.current.value = "";
      }
      
      // Refresh images list
      await fetchImages();
      
      // Call callback if provided
      if (onImageAdded) {
        onImageAdded(response.data);
      }
    } catch (err) {
      console.error("Error adding image:", err);
      setMessage({ type: "error", text: err.response?.data?.error || "Failed to add image" });
    } finally {
      setUploading(false);
    }
  };

  const handleUpdateDescription = async (imageId, image) => {
    try {
      await productService.images.update(productId, imageId, {
        angle_description: editingDescription,
        display_order: image.display_order,
      });

      setMessage({ type: "success", text: "Image updated successfully!" });
      setEditingId(null);
      setEditingDescription("");
      await fetchImages();
    } catch (err) {
      console.error("Error updating image:", err);
      setMessage({ type: "error", text: err.response?.data?.error || "Failed to update image" });
    }
  };

  const handleDeleteImage = async (imageId) => {
    if (!window.confirm("Are you sure you want to delete this image?")) {
      return;
    }

    try {
      await productService.images.remove(productId, imageId);
      setMessage({ type: "success", text: "Image deleted successfully!" });
      await fetchImages();
    } catch (err) {
      console.error("Error deleting image:", err);
      setMessage({ type: "error", text: err.response?.data?.error || "Failed to delete image" });
    }
  };

  const handleReplaceImage = async (imageId, image) => {
    const input = document.createElement("input");
    input.type = "file";
    input.accept = "image/*";
    
    input.onchange = async (e) => {
      const file = e.target.files[0];
      if (!file) return;

      if (!file.type.startsWith("image/")) {
        setMessage({ type: "error", text: "Please select a valid image file." });
        return;
      }
      if (file.size > 5 * 1024 * 1024) {
        setMessage({ type: "error", text: "Image size must be less than 5MB." });
        return;
      }

      try {
        setUploading(true);
        const formData = new FormData();
        formData.append("image", file);
        formData.append("angle_description", image.angle_description);

        await productService.images.replace(productId, imageId, formData);
        
        setMessage({ type: "success", text: "Image replaced successfully!" });
        await fetchImages();
      } catch (err) {
        console.error("Error replacing image:", err);
        setMessage({ type: "error", text: err.response?.data?.error || "Failed to replace image" });
      } finally {
        setUploading(false);
      }
    };

    input.click();
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center py-12">
        <Loader className="h-6 w-6 animate-spin text-gray-600" />
        <span className="ml-2 text-gray-600">Loading images...</span>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Messages */}
      {message && (
        <div className={`p-4 rounded-lg ${message.type === "success" ? "bg-green-50 text-green-700" : "bg-red-50 text-red-700"}`}>
          {message.text}
        </div>
      )}

      {/* Add New Image Section */}
      <div className="bg-gray-50 rounded-lg p-6 border border-gray-200">
        <h3 className="text-lg font-semibold mb-4 flex items-center gap-2">
          <Plus className="h-5 w-5" />
          Add New Image
        </h3>

        <form onSubmit={handleAddImage} className="space-y-4">
          <div>
            <label className="block text-sm font-medium mb-2">Image File</label>
            <input
              type="file"
              accept="image/*"
              onChange={handleFileChange}
              className="w-full border border-gray-300 rounded px-3 py-2"
              disabled={uploading}
            />
            <p className="text-xs text-gray-500 mt-1">Max size: 5MB. Formats: JPEG, PNG, GIF, WebP</p>
            {newImageFile && <p className="text-xs text-green-600 mt-1">âœ“ Selected: {newImageFile.name}</p>}
          </div>

          <div>
            <label className="block text-sm font-medium mb-2">Description (Angle)</label>
            <input
              type="text"
              value={newImageDescription}
              onChange={(e) => setNewImageDescription(e.target.value)}
              placeholder="e.g., Front View, Side Profile, Back View"
              className="w-full border border-gray-300 rounded px-3 py-2"
              disabled={uploading}
            />
          </div>

          <button
            type="submit"
            disabled={uploading || !newImageFile}
            className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700 disabled:bg-gray-400"
          >
            {uploading ? "Uploading..." : "Add Image"}
          </button>
        </form>
      </div>

      {/* Existing Images Section */}
      <div>
        <h3 className="text-lg font-semibold mb-4">Product Images ({images.length})</h3>

        {images.length === 0 ? (
          <div className="text-center py-8 bg-gray-50 rounded-lg border border-gray-200">
            <p className="text-gray-600">No images uploaded yet</p>
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {images.map((image) => (
              <div key={image.id} className="bg-white rounded-lg border border-gray-200 p-4">
                {/* Image Preview */}
                <div className="mb-4 relative">
                  <img
                    src={getImageUrl(image.image_path)}
                    alt={image.angle_description}
                    className="w-full h-40 object-cover rounded"
                    onError={(e) => {
                      e.target.src = "assets/img/placeholder.png";
                    }}
                  />
                  <span className="absolute top-2 left-2 bg-blue-600 text-white text-xs px-2 py-1 rounded">
                    {image.angle_description}
                  </span>
                </div>

                {/* Image Details */}
                {editingId === image.id ? (
                  <div className="space-y-3">
                    <input
                      type="text"
                      value={editingDescription}
                      onChange={(e) => setEditingDescription(e.target.value)}
                      placeholder="Edit description"
                      className="w-full text-sm border border-gray-300 rounded px-2 py-1"
                    />
                    <div className="flex gap-2">
                      <button
                        onClick={() => handleUpdateDescription(image.id, image)}
                        className="flex-1 flex items-center justify-center gap-2 px-2 py-1 bg-green-600 text-white text-sm rounded hover:bg-green-700"
                      >
                        <Save className="h-4 w-4" />
                        Save
                      </button>
                      <button
                        onClick={() => setEditingId(null)}
                        className="flex-1 flex items-center justify-center gap-2 px-2 py-1 bg-gray-300 text-gray-700 text-sm rounded hover:bg-gray-400"
                      >
                        <X className="h-4 w-4" />
                        Cancel
                      </button>
                    </div>
                  </div>
                ) : (
                  <div className="space-y-2">
                    <p className="text-sm text-gray-600">
                      <span className="font-semibold">Description:</span> {image.angle_description}
                    </p>
                    <p className="text-xs text-gray-500">
                      Order: {image.display_order}
                    </p>

                    {/* Action Buttons */}
                    <div className="flex gap-2 pt-2">
                      <button
                        onClick={() => {
                          setEditingId(image.id);
                          setEditingDescription(image.angle_description);
                        }}
                        className="flex-1 flex items-center justify-center gap-2 px-2 py-1 bg-yellow-500 text-white text-sm rounded hover:bg-yellow-600"
                        title="Edit description"
                      >
                        <Edit2 className="h-4 w-4" />
                        Edit
                      </button>
                      <button
                        onClick={() => handleReplaceImage(image.id, image)}
                        disabled={uploading}
                        className="flex-1 flex items-center justify-center gap-2 px-2 py-1 bg-blue-500 text-white text-sm rounded hover:bg-blue-600 disabled:bg-gray-400"
                        title="Replace image"
                      >
                        <Plus className="h-4 w-4" />
                        Replace
                      </button>
                      <button
                        onClick={() => handleDeleteImage(image.id)}
                        className="flex-1 flex items-center justify-center gap-2 px-2 py-1 bg-red-600 text-white text-sm rounded hover:bg-red-700"
                        title="Delete image"
                      >
                        <Trash2 className="h-4 w-4" />
                        Delete
                      </button>
                    </div>
                  </div>
                )}
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}
