import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { productService } from "../services/api";
import { getImageUrl } from "../utils/imageHelper";
import { useCart } from "../context/CartContext";
import { Heart, Share2, ChevronLeft, ChevronRight, Loader, AlertCircle } from "lucide-react";

export default function ProductDetail() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [quantity, setQuantity] = useState(1);
  const [isFavorite, setIsFavorite] = useState(false);
  const { addToCart } = useCart();

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        setLoading(true);
        const res = await productService.getById(id);
        setProduct(res.data);
        setError(null);
      } catch (err) {
        console.error("Error fetching product:", err);
        setError("Failed to load product details");
      } finally {
        setLoading(false);
      }
    };

    fetchProduct();
  }, [id]);

  const handleAddToCart = () => {
    if (!product) return;
    addToCart(product, Number(quantity || 1));
    alert(`Added ${quantity} item(s) to cart`);
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-white flex items-center justify-center">
        <div className="text-center">
          <Loader className="h-12 w-12 text-blue-600 animate-spin mx-auto mb-4" />
          <p className="text-lg text-gray-600">Loading product details...</p>
        </div>
      </div>
    );
  }

  if (error || !product) {
    return (
      <div className="min-h-screen bg-white flex items-center justify-center">
        <div className="text-center">
          <AlertCircle className="h-12 w-12 text-red-600 mx-auto mb-4" />
          <p className="text-lg text-red-600 mb-4 font-semibold">{error || "Product not found"}</p>
          <button
            onClick={() => navigate("/shop")}
            className="px-6 py-2 bg-blue-600 text-white rounded-lg font-semibold hover:bg-blue-700 transition-colors"
          >
            Back to Shop
          </button>
        </div>
      </div>
    );
  }

  const imageUrl = getImageUrl(product.image);

  return (
    <div className="min-h-screen bg-white">
      {/* Breadcrumb */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4 border-b border-gray-200">
        <button
          onClick={() => navigate(-1)}
          className="flex items-center gap-2 text-blue-600 hover:text-blue-700 font-medium transition-colors"
        >
          <ChevronLeft className="h-5 w-5" />
          Back
        </button>
      </div>

      {/* Product Section */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-12">
          {/* Image Section */}
          <div className="flex ">
            <div className="w-full  rounded-2xl p-8 flex items-center justify-center min-h-96">
              <img
                src={imageUrl}
                alt={product.name}
                className="w-full h-full object-contain max-h-96"
                onError={(e) => {
                  e.target.src = "assets/img/placeholder.png";
                }}
              />
            </div>
          </div>

          {/* Product Info Section */}
          <div className="flex flex-col justify-center">
            {/* Header */}
            <div className="mb-8">
              <p className="text-sm font-medium text-blue-600 mb-2">Product ID: #{product.id}</p>
              <h1 className="text-5xl font-bold text-gray-900 mb-3">{product.name}</h1>
              
              {/* Rating */}
              <div className="flex items-center gap-2">
                <div className="flex gap-1">
                  {[...Array(5)].map((_, i) => (
                    <span key={i} className="text-yellow-400 text-lg">★</span>
                  ))}
                </div>
                <span className="text-gray-600 text-sm font-medium">(250+ reviews)</span>
              </div>
            </div>

            {/* Price Section */}
            <div className="mb-8 pb-8 border-b border-gray-200">
              <div className="flex items-center gap-4 mb-3">
                <span className="text-5xl font-bold text-gray-900">₹{product.price}</span>
                <span className="text-lg font-semibold text-red-600 bg-red-100 px-4 py-1 rounded-lg">
                  20% OFF
                </span>
              </div>
              <p className="text-sm text-gray-600">Inclusive of all taxes</p>
            </div>

            {/* Description */}
            {product.description && (
              <div className="mb-8 pb-8 border-b border-gray-200">
                <h3 className="text-lg font-semibold text-gray-900 mb-3">About this product</h3>
                <p className="text-gray-700 leading-relaxed">{product.description}</p>
              </div>
            )}

            {/* Key Features */}
            <div className="mb-8 pb-8 border-b border-gray-200">
              <h3 className="text-lg font-semibold text-gray-900 mb-4">Key Features</h3>
              <ul className="space-y-3">
                <li className="flex items-center gap-3 text-gray-700">
                  <span className="w-2 h-2 bg-blue-600 rounded-full"></span>
                  Premium quality materials
                </li>
                <li className="flex items-center gap-3 text-gray-700">
                  <span className="w-2 h-2 bg-blue-600 rounded-full"></span>
                  Long-lasting durability
                </li>
                <li className="flex items-center gap-3 text-gray-700">
                  <span className="w-2 h-2 bg-blue-600 rounded-full"></span>
                  Easy to use and maintain
                </li>
                <li className="flex items-center gap-3 text-gray-700">
                  <span className="w-2 h-2 bg-blue-600 rounded-full"></span>
                  Environmentally friendly
                </li>
              </ul>
            </div>

            {/* Quantity Selector */}
            <div className="mb-8">
              <label className="block text-sm font-semibold text-gray-900 mb-3">Quantity</label>
              <div className="flex items-center gap-4 w-fit">
                <button
                  onClick={() => setQuantity(Math.max(1, quantity - 1))}
                  className="w-12 h-12 border border-gray-300 rounded-lg flex items-center justify-center hover:bg-gray-100 hover:border-gray-400 transition-colors font-bold text-gray-900"
                >
                  −
                </button>
                <input
                  type="number"
                  value={quantity}
                  onChange={(e) => setQuantity(Math.max(1, parseInt(e.target.value) || 1))}
                  className="w-20 px-4 py-2 border border-gray-300 rounded-lg text-center font-semibold focus:outline-none focus:ring-2 focus:ring-blue-600 focus:border-transparent"
                  min="1"
                />
                <button
                  onClick={() => setQuantity(quantity + 1)}
                  className="w-12 h-12 border border-gray-300 rounded-lg flex items-center justify-center hover:bg-gray-100 hover:border-gray-400 transition-colors font-bold text-gray-900"
                >
                  +
                </button>
              </div>
            </div>

            {/* Action Buttons */}
            <div className="grid grid-cols-1 sm:grid-cols-3 gap-4 mb-8">
              <button
                onClick={handleAddToCart}
                className="sm:col-span-2 px-6 py-4 bg-blue-600 text-white rounded-lg font-semibold text-lg hover:bg-blue-700 transition-colors shadow-lg hover:shadow-xl"
              >
                Add to Cart
              </button>
              <button
                onClick={() => setIsFavorite(!isFavorite)}
                className={`px-6 py-4 rounded-lg font-semibold transition-all border-2 flex items-center justify-center gap-2 ${
                  isFavorite
                    ? "bg-red-50 border-red-200 text-red-600 hover:bg-red-100"
                    : "bg-gray-100 border-gray-300 text-gray-900 hover:bg-gray-200 hover:border-gray-400"
                }`}
              >
                <Heart className={`h-5 w-5 ${isFavorite ? "fill-red-600" : ""}`} />
              </button>
            </div>

            {/* Share Button */}
            <button className="px-6 py-3 border border-gray-300 text-gray-900 rounded-lg font-semibold hover:bg-gray-50 transition-colors flex items-center justify-center gap-2">
              <Share2 className="h-5 w-5" />
              Share
            </button>

            {/* Trust Section */}
            <div className="mt-8 pt-8 border-t border-gray-200 grid grid-cols-3 gap-4">
              <div className="text-center">
                <p className="text-2xl font-bold text-blue-600 mb-1">100%</p>
                <p className="text-xs text-gray-600 font-medium">Authentic</p>
              </div>
              <div className="text-center">
                <p className="text-2xl font-bold text-blue-600 mb-1">30 Days</p>
                <p className="text-xs text-gray-600 font-medium">Easy Return</p>
              </div>
              <div className="text-center">
                <p className="text-2xl font-bold text-blue-600 mb-1">24/7</p>
                <p className="text-xs text-gray-600 font-medium">Support</p>
              </div>
            </div>
          </div>
        </div>

        {/* Delivery & Info Section */}
        <div className="mt-16 pt-12 border-t border-gray-200 grid grid-cols-1 md:grid-cols-2 gap-8">
          <div className="bg-blue-50 border border-blue-200 rounded-xl p-6">
            <h3 className="text-lg font-semibold text-gray-900 mb-4">Delivery Information</h3>
            <ul className="space-y-3 text-sm text-gray-700">
              <li className="flex items-start gap-3">
                <span className="text-blue-600 font-bold mt-0.5">✓</span>
                <span>Free delivery on orders above ₹500</span>
              </li>
              <li className="flex items-start gap-3">
                <span className="text-blue-600 font-bold mt-0.5">✓</span>
                <span>Delivery within 3-5 business days</span>
              </li>
              <li className="flex items-start gap-3">
                <span className="text-blue-600 font-bold mt-0.5">✓</span>
                <span>Track your order in real-time</span>
              </li>
            </ul>
          </div>

          <div className="bg-green-50 border border-green-200 rounded-xl p-6">
            <h3 className="text-lg font-semibold text-gray-900 mb-4">Product Details</h3>
            <ul className="space-y-3 text-sm text-gray-700">
              <li className="flex justify-between">
                <span className="font-medium">Availability:</span>
                <span className="text-green-600 font-semibold">In Stock</span>
              </li>
              <li className="flex justify-between">
                <span className="font-medium">SKU:</span>
                <span>PROD-{product.id}</span>
              </li>
              <li className="flex justify-between">
                <span className="font-medium">Category:</span>
                <span>General</span>
              </li>
            </ul>
          </div>
        </div>
      </div>

      {/* Related Products Section */}
      <div className="bg-gray-50 border-t border-gray-200 py-16 mt-12">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <h2 className="text-3xl font-bold text-gray-900 mb-6">You May Also Like</h2>
          <div className="text-center py-12 text-gray-600">
            <p>Related products coming soon...</p>
          </div>
        </div>
      </div>
    </div>
  );
}
