import React, { useState, useEffect } from "react";
import axios from "axios";
import { ChevronLeft, ChevronRight, Loader, AlertCircle } from "lucide-react";
import ProductCard from "../common/ProductCard";

export default function ProductSlider() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [itemsPerPage, setItemsPerPage] = useState(4);

  const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5000";

  // Detect screen size
  useEffect(() => {
    const handleResize = () => {
      if (window.innerWidth < 640) setItemsPerPage(1);
      else if (window.innerWidth < 1024) setItemsPerPage(2);
      else if (window.innerWidth < 1280) setItemsPerPage(3);
      else setItemsPerPage(4);
    };

    handleResize();
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      setLoading(true);
      const response = await axios.get(`${API_URL}/products`);
      setProducts(response.data || []);
      setError(null);
    } catch (err) {
      console.error("Error fetching products:", err);
      setError("Failed to load products");
    } finally {
      setLoading(false);
    }
  };

  const handlePrevious = () => {
    setCurrentIndex((prev) =>
      prev === 0 ? Math.max(0, products.length - itemsPerPage) : prev - 1
    );
  };

  const handleNext = () => {
    setCurrentIndex((prev) =>
      prev + itemsPerPage >= products.length ? 0 : prev + 1
    );
  };

  // Auto-scroll every 5 seconds
  useEffect(() => {
    if (products.length === 0) return;
    const interval = setInterval(handleNext, 5000);
    return () => clearInterval(interval);
  }, [products.length, itemsPerPage]);

  if (loading) {
    return (
      <section className="py-12 sm:py-16 lg:py-20 bg-white">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-center items-center py-24">
            <Loader className="h-10 w-10 text-blue-600 animate-spin" />
          </div>
        </div>
      </section>
    );
  }

  if (error || products.length === 0) {
    return null;
  }

  const visibleProducts = products.slice(currentIndex, currentIndex + itemsPerPage);

  return (
    <section className="py-12 sm:py-16 lg:py-20 bg-gradient-to-b from-gray-50 to-white">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="mb-12">
          <div className="flex items-center justify-between mb-4">
            <div>
              <h2 className="text-2xl sm:text-3xl lg:text-4xl font-bold text-gray-900 mb-2">
                Featured Products
              </h2>
              <p className="text-sm sm:text-base text-gray-600">
                Swipe through our amazing collection
              </p>
            </div>
          </div>
          <div className="h-1 w-16 bg-gradient-to-r from-blue-600 to-indigo-600 rounded-full" />
        </div>

        {/* Slider Container */}
        <div className="relative">
          {/* Products Grid */}
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-3 sm:gap-4 lg:gap-6">
            {visibleProducts.map((product) => (
              <div
                key={product.id}
                className="transition-all duration-300 ease-in-out transform hover:scale-105"
              >
                <ProductCard product={product} />
              </div>
            ))}
          </div>

          {/* Previous Button */}
          <button
            onClick={handlePrevious}
            className="absolute -left-4 sm:-left-6 lg:-left-8 top-1/3 transform -translate-y-1/2 z-10 bg-white hover:bg-blue-600 text-gray-900 hover:text-white rounded-full p-2 sm:p-3 shadow-lg transition-all duration-300 hover:shadow-xl"
            aria-label="Previous products"
          >
            <ChevronLeft className="h-5 w-5 sm:h-6 sm:w-6" />
          </button>

          {/* Next Button */}
          <button
            onClick={handleNext}
            className="absolute -right-4 sm:-right-6 lg:-right-8 top-1/3 transform -translate-y-1/2 z-10 bg-white hover:bg-blue-600 text-gray-900 hover:text-white rounded-full p-2 sm:p-3 shadow-lg transition-all duration-300 hover:shadow-xl"
            aria-label="Next products"
          >
            <ChevronRight className="h-5 w-5 sm:h-6 sm:w-6" />
          </button>
        </div>

        {/* Slider Indicators */}
        <div className="flex justify-center items-center gap-2 mt-8">
          {Array.from({
            length: Math.ceil(products.length / itemsPerPage),
          }).map((_, index) => (
            <button
              key={index}
              onClick={() =>
                setCurrentIndex(
                  Math.min(index * itemsPerPage, products.length - itemsPerPage)
                )
              }
              className={`h-2 rounded-full transition-all duration-300 ${
                Math.floor(currentIndex / itemsPerPage) === index
                  ? "bg-blue-600 w-8"
                  : "bg-gray-300 w-2 hover:bg-gray-400"
              }`}
              aria-label={`Go to slide ${index + 1}`}
            />
          ))}
        </div>

        {/* Product Counter */}
        <div className="text-center mt-6">
          <p className="text-sm sm:text-base text-gray-600 font-medium">
            Showing {currentIndex + 1} - {Math.min(currentIndex + itemsPerPage, products.length)} of{" "}
            {products.length} products
          </p>
        </div>
      </div>
    </section>
  );
}
