import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { Heart, ShoppingCart, Loader, AlertCircle, ArrowRight } from "lucide-react";
import { getImageUrl } from "../../utils/imageHelper";

export default function RecentProducts() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [favorites, setFavorites] = useState(new Set());
  const navigate = useNavigate();

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await axios.get("http://localhost:5000/products");
      // Get last 6 products (most recent)
      const recentProducts = response.data.slice(-6).reverse();
      setProducts(recentProducts);
    } catch (err) {
      console.error("Error fetching products:", err);
      setError("Failed to load recent products");
    } finally {
      setLoading(false);
    }
  };

  const toggleFavorite = (productId) => {
    setFavorites(prev => {
      const newFav = new Set(prev);
      if (newFav.has(productId)) {
        newFav.delete(productId);
      } else {
        newFav.add(productId);
      }
      return newFav;
    });
  };

  if (error) {
    return (
      <div className="flex items-center gap-3 p-4 bg-red-50 border border-red-200 rounded-lg max-w-md">
        <AlertCircle className="h-5 w-5 text-red-600" />
        <p className="text-red-700">{error}</p>
      </div>
    );
  }

  return (
    <div className="py-16 bg-gray-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="mb-12">
          <div className="flex items-center justify-between mb-4">
            <div>
              <h2 className="text-3xl md:text-4xl font-bold text-gray-900 mb-2">
                Recently Added
              </h2>
              <p className="text-gray-600">
                Discover our latest products handpicked for you
              </p>
            </div>
            <button
              onClick={() => navigate("/shop")}
              className="hidden md:flex items-center gap-2 px-6 py-3 bg-blue-600 text-white font-semibold rounded-lg hover:bg-blue-700 transition-colors"
            >
              View All
              <ArrowRight className="h-5 w-5" />
            </button>
          </div>
          <div className="h-1 w-16 bg-gradient-to-r from-blue-600 to-indigo-600 rounded-full" />
        </div>

        {/* Loading State */}
        {loading ? (
          <div className="flex justify-center items-center py-16">
            <Loader className="h-8 w-8 text-blue-600 animate-spin" />
          </div>
        ) : products.length === 0 ? (
          <div className="text-center py-12">
            <p className="text-gray-600 font-medium mb-4">No products available</p>
            <button
              onClick={() => navigate("/shop")}
              className="px-6 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
            >
              Browse Shop
            </button>
          </div>
        ) : (
          <>
            {/* Products Grid */}
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6 mb-12">
              {products.map((product) => (
                <div
                  key={product.id}
                  className="group bg-white border border-gray-200 rounded-lg overflow-hidden hover:shadow-lg transition-shadow duration-300"
                >
                  {/* Image Container */}
                  <div
                    className="relative h-80 overflow-hidden bg-gray-100 cursor-pointer"
                    onClick={() => navigate(`/product/${product.id}`)}
                  >
                    <img
                      src={getImageUrl(product.image)}
                      alt={product.name}
                      className="w-full h-full object-cover group-hover:scale-110 transition-transform duration-300"
                      onError={(e) => {
                        e.target.src = "assets/img/placeholder.png";
                      }}
                    />

                    {/* Favorite Button */}
                    <button
                      onClick={(e) => {
                        e.stopPropagation();
                        toggleFavorite(product.id);
                      }}
                      className="absolute top-3 right-3 p-2 bg-white rounded-full shadow-md hover:shadow-lg transition-all"
                    >
                      <Heart
                        className={`h-5 w-5 ${
                          favorites.has(product.id) ? "fill-red-500 text-red-500" : "text-gray-400 hover:text-red-500"
                        }`}
                      />
                    </button>

                    {/* Overlay - Add to Cart */}
                    <div className="absolute inset-0 bg-black bg-opacity-0 group-hover:bg-opacity-40 flex items-end justify-center p-4 transition-all duration-300">
                      <button
                        onClick={(e) => {
                          e.stopPropagation();
                        }}
                        className="w-full px-4 py-3 bg-blue-600 text-white rounded-lg font-semibold hover:bg-blue-700 transition-colors flex items-center justify-center gap-2 opacity-0 group-hover:opacity-100 transition-opacity"
                      >
                        <ShoppingCart className="h-5 w-5" />
                        Add to Cart
                      </button>
                    </div>
                  </div>

                  {/* Product Info */}
                  <div className="p-4">
                    <div className="text-sm font-medium text-gray-500 mb-1">Product</div>
                    <h3
                      onClick={() => navigate(`/product/${product.id}`)}
                      className="block text-lg font-semibold text-gray-900 truncate hover:text-blue-600 transition-colors mb-2 cursor-pointer"
                    >
                      {product.name}
                    </h3>
                    <div className="flex items-center justify-between mb-4">
                      <p className="text-2xl font-bold text-gray-900">â‚¹{parseFloat(product.price).toFixed(2)}</p>
                    </div>
                    <button
                      onClick={() => navigate(`/product/${product.id}`)}
                      className="block w-full py-2 bg-gray-100 text-gray-900 rounded-lg font-semibold hover:bg-gray-200 transition-colors text-center text-sm"
                    >
                      View Details
                    </button>
                  </div>
                </div>
              ))}
            </div>

            {/* View All Button (Mobile) */}
            <div className="flex justify-center md:hidden">
              <button
                onClick={() => navigate("/shop")}
                className="flex items-center gap-2 px-6 py-3 bg-blue-600 text-white font-semibold rounded-lg hover:bg-blue-700 transition-colors"
              >
                View All Products
                <ArrowRight className="h-5 w-5" />
              </button>
            </div>
          </>
        )}
      </div>
    </div>
  );
}
