import React, { useEffect, useState } from "react";
import axios from "axios";
import ProductCard from "../common/ProductCard";
import { Loader, TrendingUp } from "lucide-react";

export default function BestsellersSection() {
  const [bestsellers, setBestsellers] = useState([]);
  const [loading, setLoading] = useState(true);

  const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5000";

  useEffect(() => {
    const fetchBestsellers = async () => {
      try {
        setLoading(true);
        // Try to get bestsellers
        const response = await axios.get(`${API_URL}/bestsellers/top/8`);
        const data = response.data || [];
        
        // If no bestsellers, fetch regular products instead
        if (data.length === 0) {
          try {
            const productsResponse = await axios.get(`${API_URL}/products`);
            const products = (productsResponse.data || []).slice(0, 8);
            setBestsellers(products);
          } catch (err) {
            console.error("Error fetching products:", err);
            setBestsellers([]);
          }
        } else {
          setBestsellers(data);
        }
      } catch (err) {
        console.error("Error fetching bestsellers:", err);
        // Fallback to regular products
        try {
          const productsResponse = await axios.get(`${API_URL}/products`);
          const products = (productsResponse.data || []).slice(0, 8);
          setBestsellers(products);
        } catch (err2) {
          console.error("Error fetching products:", err2);
          setBestsellers([]);
        }
      } finally {
        setLoading(false);
      }
    };

    fetchBestsellers();
  }, []);

  if (loading) {
    return (
      <section className="py-12 sm:py-16 lg:py-20 bg-gradient-to-b from-white to-gray-50">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex flex-col items-center justify-center py-24">
            <Loader className="h-10 w-10 text-blue-600 animate-spin mb-4" />
            <p className="text-lg text-gray-600">Loading products...</p>
          </div>
        </div>
      </section>
    );
  }

  // Show nothing if no products at all
  if (!bestsellers || bestsellers.length === 0) {
    return null;
  }

  return (
    <section className="py-12 sm:py-16 lg:py-20 bg-gradient-to-b from-white to-gray-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Section Header */}
        <div className="text-center mb-12">
          <div className="flex items-center justify-center gap-2 mb-4">
            <TrendingUp className="h-6 w-6 sm:h-8 sm:w-8 text-orange-500" />
            <h2 className="text-2xl sm:text-3xl lg:text-4xl font-bold text-gray-900">
              Bestsellers
            </h2>
            <TrendingUp className="h-6 w-6 sm:h-8 sm:w-8 text-orange-500" />
          </div>
          <p className="text-sm sm:text-base lg:text-lg text-gray-600 max-w-2xl mx-auto">
            Most popular products loved by our customers
          </p>
        </div>

        {/* Products Grid */}
        <div className="grid grid-cols-2 sm:grid-cols-2 lg:grid-cols-4 gap-3 sm:gap-4 lg:gap-6">
          {bestsellers.map((product) => (
            <div key={product.id} className="relative">
              {/* Bestseller Badge - only show if product has sales */}
              {product.total_sold && product.total_sold > 0 && (
                <div className="absolute top-2 right-2 z-10 bg-orange-500 text-white px-2 py-1 rounded-full text-xs font-bold">
                  {product.total_sold} sold
                </div>
              )}
              <ProductCard product={product} />
            </div>
          ))}
        </div>

        {/* Call to Action */}
        <div className="text-center mt-12">
          <a
            href="/shop"
            className="inline-block px-6 sm:px-8 py-2 sm:py-3 bg-gradient-to-r from-orange-500 to-red-500 text-white font-semibold rounded-lg hover:shadow-lg transform hover:scale-105 transition-all duration-300"
          >
            View All Products
          </a>
        </div>
      </div>
    </section>
  );
}
