import React, { useState, useEffect } from "react";
import axios from "axios";
import { ChevronLeft, ChevronRight, Loader } from "lucide-react";
import ProductCard from "../common/ProductCard";

export default function ProductSlider() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [itemsPerPage, setItemsPerPage] = useState(4);
  const [isAutoPlay, setIsAutoPlay] = useState(true);

  const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5000";

  // Detect screen size
  useEffect(() => {
    const handleResize = () => {
      if (window.innerWidth < 640) setItemsPerPage(1);
      else if (window.innerWidth < 1024) setItemsPerPage(2);
      else if (window.innerWidth < 1280) setItemsPerPage(3);
      else setItemsPerPage(4);
    };

    handleResize();
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      setLoading(true);
      const response = await axios.get(`${API_URL}/products`);
      setProducts(response.data || []);
      setError(null);
    } catch (err) {
      console.error("Error fetching products:", err);
      setError("Failed to load products");
    } finally {
      setLoading(false);
    }
  };

  const handlePrevious = () => {
    setIsAutoPlay(false);
    setCurrentIndex((prev) => {
      if (prev === 0) {
        return Math.max(0, products.length - itemsPerPage);
      }
      return prev - 1;
    });
  };

  const handleNext = () => {
    setIsAutoPlay(false);
    setCurrentIndex((prev) => {
      const maxIndex = Math.max(0, products.length - itemsPerPage);
      if (prev >= maxIndex) {
        return 0;
      }
      return prev + 1;
    });
  };

  // Auto-scroll with smooth loop
  useEffect(() => {
    if (!isAutoPlay || products.length === 0) return;
    
    const interval = setInterval(() => {
      setCurrentIndex((prev) => {
        const maxIndex = Math.max(0, products.length - itemsPerPage);
        if (prev >= maxIndex) {
          return 0; // Loop back to start
        }
        return prev + 1;
      });
    }, 4000); // Change slide every 4 seconds

    return () => clearInterval(interval);
  }, [isAutoPlay, products.length, itemsPerPage]);

  // Resume autoplay after 10 seconds of no interaction
  useEffect(() => {
    if (isAutoPlay) return;

    const timeout = setTimeout(() => {
      setIsAutoPlay(true);
    }, 10000);

    return () => clearTimeout(timeout);
  }, [isAutoPlay]);

  if (loading) {
    return (
      <section className="py-12 sm:py-16 lg:py-20 bg-white">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-center items-center py-24">
            <Loader className="h-10 w-10 text-blue-600 animate-spin" />
          </div>
        </div>
      </section>
    );
  }

  if (error || products.length === 0) {
    return null;
  }

  const visibleProducts = products.slice(
    currentIndex,
    currentIndex + itemsPerPage
  );

  return (
    <section 
      className="py-12 sm:py-16 lg:py-20 bg-gradient-to-b from-gray-50 to-white"
      onMouseEnter={() => setIsAutoPlay(false)}
      onMouseLeave={() => setIsAutoPlay(true)}
    >
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="mb-12">
          <div className="flex items-center justify-between mb-4">
            <div>
              <h2 className="text-2xl sm:text-3xl lg:text-4xl font-bold text-gray-900 mb-2">
                Featured Products
              </h2>
              <p className="text-sm sm:text-base text-gray-600">
                Swipe through our amazing collection
              </p>
            </div>
          </div>
          <div className="h-1 w-16 bg-gradient-to-r from-blue-600 to-indigo-600 rounded-full" />
        </div>

        {/* Slider Container */}
        <div className="relative group">
          {/* Products Grid with Smooth Transition */}
          <div className="overflow-hidden">
            <div
              className="flex transition-transform duration-700 ease-in-out"
              style={{
                transform: `translateX(-${(currentIndex / itemsPerPage) * 100}%)`,
              }}
            >
              {/* Create extended carousel for infinite loop */}
              {[...Array(Math.ceil(products.length / itemsPerPage))].map(
                (_, groupIndex) => (
                  <div
                    key={`group-${groupIndex}`}
                    className="min-w-full flex"
                  >
                    {products
                      .slice(
                        groupIndex * itemsPerPage,
                        groupIndex * itemsPerPage + itemsPerPage
                      )
                      .map((product) => (
                        <div
                          key={product.id}
                          className={`flex-1 transition-all duration-300 ${
                            itemsPerPage === 1
                              ? "w-full"
                              : itemsPerPage === 2
                              ? "w-1/2"
                              : itemsPerPage === 3
                              ? "w-1/3"
                              : "w-1/4"
                          }`}
                        >
                          <div className="px-2 sm:px-3 lg:px-4">
                            <ProductCard product={product} />
                          </div>
                        </div>
                      ))}
                  </div>
                )
              )}
            </div>
          </div>

          {/* Previous Button - Hidden on small screens */}
          <button
            onClick={handlePrevious}
            className="absolute -left-4 sm:-left-6 lg:-left-8 top-1/3 transform -translate-y-1/2 z-10 bg-white hover:bg-blue-600 text-gray-900 hover:text-white rounded-full p-2 sm:p-3 shadow-lg transition-all duration-300 hover:shadow-xl opacity-0 group-hover:opacity-100"
            aria-label="Previous products"
          >
            <ChevronLeft className="h-5 w-5 sm:h-6 sm:w-6" />
          </button>

          {/* Next Button - Hidden on small screens */}
          <button
            onClick={handleNext}
            className="absolute -right-4 sm:-right-6 lg:-right-8 top-1/3 transform -translate-y-1/2 z-10 bg-white hover:bg-blue-600 text-gray-900 hover:text-white rounded-full p-2 sm:p-3 shadow-lg transition-all duration-300 hover:shadow-xl opacity-0 group-hover:opacity-100"
            aria-label="Next products"
          >
            <ChevronRight className="h-5 w-5 sm:h-6 sm:w-6" />
          </button>
        </div>

        {/* Slider Indicators */}
        <div className="flex justify-center items-center gap-2 mt-8">
          {Array.from({
            length: Math.ceil(products.length / itemsPerPage),
          }).map((_, index) => (
            <button
              key={index}
              onClick={() => {
                setIsAutoPlay(false);
                setCurrentIndex(index);
              }}
              className={`h-3 rounded-full transition-all duration-500 ${
                currentIndex === index
                  ? "bg-blue-600 w-8"
                  : "bg-gray-300 w-3 hover:bg-gray-400"
              }`}
              aria-label={`Go to slide ${index + 1}`}
            />
          ))}
        </div>

        {/* Product Counter */}
        <div className="text-center mt-6">
          <p className="text-sm sm:text-base text-gray-600 font-medium">
            Slide {Math.floor(currentIndex / itemsPerPage) + 1} of{" "}
            {Math.ceil(products.length / itemsPerPage)}
          </p>
        </div>

        {/* Auto-play indicator */}
        {isAutoPlay && (
          <div className="text-center mt-2">
            <span className="inline-block px-3 py-1 text-xs sm:text-sm bg-blue-100 text-blue-700 rounded-full font-medium">
              ‚óè Auto-playing
            </span>
          </div>
        )}
      </div>
    </section>
  );
}
